	mud.send_message(id, "||----------------------------------------------||")
	mud.send_message(id, "|| Gold: 		 {}								  ||".format(players[id].gold))
	mud.send_message(id, "||----------------------------------------------||")
	mud.send_message(id, "|| Strength:   {}         Mining:          {}    ||".format(players[id].strength, players[id].mining))
