	mud.send_message(id, "|| Wisdom:     {}         Foresting:       {}    ||".format(players[id].wisdom, players[id].foresting))
	mud.send_message(id, "|| Defence:    {}         Cooking:         {}    ||".format(players[id].defence, players[id].cooking))
