	mud.send_message(id, "|| Legs:       {}        Boots: 			{}    ||".format(players[id].legs, players[id].boots))
	mud.send_message(id, "|| Necklace:   {}        Ring: 			{}    ||".format(players[id].necklace, players[id].ring))
